def probability_rain_and_go_outside (p_rain : ‚Ñù) (p_go_outside_if_rain : ‚Ñù) : ‚Ñù :=
p_rain * p_go_outside_if_rain

def probability_no_rain_and_go_outside (p_rain : ‚Ñù) (p_go_outside_if_no_rain : ‚Ñù) : ‚Ñù :=
(1 - p_rain) * p_go_outside_if_no_rain

def total_probability_go_outside (p_rain p_go_outside_if_rain p_go_outside_if_no_rain : ‚Ñù) : ‚Ñù :=
probability_rain_and_go_outside p_rain p_go_outside_if_rain + probability_no_rain_and_go_outside p_rain p_go_outside_if_no_rain
-- Applying the example values
theorem example_26_12 : total_probability_go_outside 0.20 0.10 0.80 = 0.66 :=
by rw [total_probability_go_outside, probability_rain_and_go_outside, probability_no_rain_and_go_outside, mul_comm 0.20 0.10, mul_comm (1 - 0.20) 0.80, mul_one 0.10, mul_one 0.80, sub_self, zero_add]
